import React from "react";
import Accordion from './Accordion'
import { quesAns } from "./AccordionApi";


const App = () => {
    return  (
        <>
            <div className="main-div">
                <div className="center-div">
                    {quesAns.map((item) =>{
                        return <Accordion questions={item.question} answers={item.answer}/>
                    })}
                </div>
            </div>
        </>
    )
};

export default App;
