import React, { useState } from 'react';
import "../css/CreateNotes.css";
import { Button } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add'

const CreateNotes = () => {

    const createNote = (e) => {
        const {name,value} = e.target;
        setNote((oldValue) => {
            return {
              ...oldValue,
                [name]:value
            }
        })
    }
    const addNotes = () => {
        setAllNotes((preValue) => {
            return {
                ...preValue,
                note
            }
        })
    }
    return (
        <>
            <div className='createNotes-container'>
                <div className='addNoteBox'>
                    <input
                        type='text'
                        placeholder='Title'
                        onChange={createNote}
                        name='title'

                    />
                    <textarea
                     placeholder='Enter Your Content Here' 
                     onChange={createNote}
                     name='content'
                    />
                    <Button className="addBtn" onClick={addNotes}><AddIcon /></Button>
                </div>
            </div>
        </>
    );
};

export default CreateNotes;
